import { Component, EventEmitter, Output } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
	selector: 'app-sign-in-form',
	templateUrl: './sign-in-form.component.html',
	styleUrls: ['./sign-in-form.component.scss'],
})
export class SignInFormComponent {
	// show/hide password
	showPassword = false;
	input = 'password';
	toggleShow() {
		this.showPassword = !this.showPassword;
		this.input = this.showPassword ? 'text' : 'password';
	}

	// form validation

	loginForm = new FormGroup({
		email: new FormControl('', [Validators.required, Validators.pattern('^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$')]),
		password: new FormControl('', Validators.required),
	});

	email: string = '';
	password: string = '';



	// login2() {
	// 	alert(this.loginForm.value.email + ' | ' + this.loginForm.value.password);
	// }

	@Output() emailSubmitted = new EventEmitter();
	@Output() passwordSubmitted = new EventEmitter();

	errorEmail = '';

	onSubmit() {
		if (this.loginForm.valid) {
			const email = this.loginForm.value.email;
			this.emailSubmitted.emit(email);

			const password = this.loginForm.value.password;
			this.passwordSubmitted.emit(password);

			console.log(this.loginForm.value);
		} else {
			const emailControl = this.loginForm.get('email');
			if (emailControl && !emailControl.valid) {
				this.errorEmail = 'Please provide a valid email';
			}
		}
	}
}
<form
	[formGroup]="loginForm"
	(ngSubmit)="onSubmit()"
	class="flex flex-col items-center justify-center gap-6"
>
	<div class="flex flex-col">
		<label
			for="email"
			class="label-form"
			>Email</label
		>
		<input
			type="email"
			formControlName="email"
			placeholder="example@email.com"
			class="input-group"
			required

		/>
		<p class="error-text">{{errorEmail}}</p>
	</div>
	<div class="flex flex-col">
		<label
			for="password"
			class="label-form"
			>Password</label
		>
		<div class="input-group w-full">
			<input
				type="{{ input }}"
				formControlName="password"
				placeholder="······"
				style="font-size: large"
				id="password"
				required
				class="w-4/5"
			/>
			<button
				type="button"
				class="btn btn-outline-secondary"
				(click)="toggleShow()"
				class="btn-show ml-6"
			>
				<span class="eye"></span>
			</button>
		</div>
	</div>
	<div class=""
	>
		<input
			type="submit"
			value="SIGN IN"

			class="submit-login"
		/>
		<p class="error-text">
			Invalid email or password
		</p>
	</div>
</form>
