import { Component, EventEmitter, Output } from '@angular/core';
import {
	AbstractControl,
	FormGroup,
	FormControl,
	Validators,
} from '@angular/forms';
import { RegisterService } from '../../services/register/register.service';
@Component({
	selector: 'app-sing-up-form',
	templateUrl: './sing-up-form.component.html',
	styleUrls: ['./sing-up-form.component.scss'],
})
export class SingUpFormComponent {
	constructor(private registerService: RegisterService) {}

	// show/hide password
	showPassword = false;
	input = 'password';
	input2 = 'password';

	toggleShow() {
		this.showPassword = !this.showPassword;
		this.input = this.showPassword ? 'text' : 'password';
	}

	toggleShow2() {
		this.showPassword = !this.showPassword;
		this.input2 = this.showPassword ? 'text' : 'password';
	}

	// form validation
	passwordMatchValidator(control: AbstractControl) {
		const g = control as FormGroup;
		return g.get('password')?.value === g.get('confirmPassword')?.value
			? null
			: { mismatch: true };
	}

	loginForm = new FormGroup(
		{
			fullName: new FormControl('', Validators.required),
			email: new FormControl('', [
				Validators.required,
				Validators.pattern('^[a-z0-9._%+-]+@[a-z0-9.-]+.[a-z]{2,4}$'),
			]),
			password: new FormControl('', [
				Validators.required,
				// Validators.minLength(8),
				// Validators.pattern(
				// 	'^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,}$'
				// ),
			]),
			confirmPassword: new FormControl('', Validators.required),
		},
		{ validators: this.passwordMatchValidator }
	);

	errorFullName = '';
	errorEmail = '';
	errorPassword = '';
	errorConfirmPassword = '';

	@Output() fullNameSubmitted = new EventEmitter();
	@Output() emailSubmitted = new EventEmitter();
	@Output() passwordSubmitted = new EventEmitter();

	onSubmit() {
		if (this.loginForm.valid) {
			const email = this.loginForm.value.email;
			this.emailSubmitted.emit(email);

			const password = this.loginForm.value.password;
			this.passwordSubmitted.emit(password);

			console.log(this.loginForm.value);

			this.registerService.register(this.loginForm.value).subscribe(
				(res) => {

					//* TODO: redirect to login page
					console.log(res);
				},
				(error) => {
					console.log(error);
				}
			);
		} else {
			const nameControl = this.loginForm.get('fullName');
			const emailControl = this.loginForm.get('email');
			const passwordControl = this.loginForm.get('password');
			const confirmPasswordControl = this.loginForm.get('confirmPassword');

			if (nameControl && !nameControl.valid) {
				this.errorFullName = 'Please provide a valid name';
			}

			if (emailControl && !emailControl.valid) {
				this.errorEmail = 'Please provide a valid email';
			}

			if (passwordControl && !passwordControl.valid) {
				this.errorPassword = 'Please provide a password';
			}

			if (confirmPasswordControl && !confirmPasswordControl.valid) {
				this.errorConfirmPassword = 'Please confirm your password';
			}
		}
	}
}
<form
	[formGroup]="loginForm"
	(ngSubmit)="onSubmit()"
	class="flex flex-col items-center justify-center gap-6"
>
    <div class="flex flex-col">
        <label
            for="fullName"
            class="label-form"
        >
            Full Name
        </label>
        <input
            type="text"
            formControlName="fullName"
            placeholder="full Name"
            class="input-group"
            required
        />
		<p class="error-text">{{ errorFullName }}</p>
    </div>
	<div class="flex flex-col">
		<label
			for="email"
			class="label-form"
		>
			Email
		</label>
		<input
			type="email"
			formControlName="email"
			placeholder="example@email.com"
			class="input-group"
			required
		/>
        <p class="error-text">{{ errorEmail }}</p>
	</div>
	<div class="flex flex-col">
		<label
			for="password"
			class="label-form"
		>
			Password
		</label
		>
		<div class="input-group w-full">
			<input
				type="{{ input }}"
				formControlName="password"
				placeholder="······"
				style="font-size: large"
				id="password"
				required
				class="w-4/5"
			/>
			<button
				type="button"
				class="btn btn-outline-secondary"
				(click)="toggleShow()"
				class="btn-show ml-6"
			>
				<img
					src="../../../assets/eye.svg"
					alt="eye"
					class="eye w-5 h-5"
				/>
			</button>
			<p class="error-text">{{ errorPassword }}</p>
		</div>
	</div>
    <div class="flex flex-col">
        <label
            for="confirmPassword"
            class="label-form"
        >
            Confirm Password
        </label>
        <div class="input-group w-full">
            <input
                type="{{ input2 }}"
                formControlName="confirmPassword"
                placeholder="······"
                style="font-size: large"
                id="confirmPassword"
                required
                class="w-4/5"
            />
            <button
                type="button"
                class="btn btn-outline-secondary"
                (click)="toggleShow2()"
                class="btn-show ml-6"
            >
                <img
					src="../../../assets/eye.svg"
					alt="eye"
					class="eye w-5 h-5"
				/>
            </button>
			<p class="error-text">{{ errorConfirmPassword }}</p>
        </div>
    </div>
	<div>
		<input
			type="submit"
			value="SIGN UP"
			class="submit-login"
		/>
	</div>
</form>
