import { Component } from '@angular/core';
import { NOTES } from 'src/notes';
import { AllMessagesService } from '../../services/allMessages/all-messages.service';

@Component({
	selector: 'app-notes-list',
	templateUrl: './notes-list.component.html',
	styleUrls: ['./notes-list.component.scss'],
})
export class NotesListComponent {
	constructor(private allMessages: AllMessagesService) {}
	notes = NOTES;
	search = '';

	ngOnInit(): void {
		this.allMessages.getAllMessages().subscribe(
			(res: any) => {
				this.notes = res.data;
			},
			(error) => {
				console.log(error);
			}
		);
	}

	// search notes, filter by title or body text (case insensitive) and show results
	searchNotes() {
		if (this.search) {
			this.notes = this.notes.filter((note) => {
				note.title?.toLowerCase().includes(this.search.toLowerCase()) ||
				note.body?.toLowerCase().includes(this.search.toLowerCase())

				console.log(note.title?.toLowerCase().includes(this.search.toLowerCase()) ||
				note.body?.toLowerCase().includes(this.search.toLowerCase()));
				return (
					note.title?.toLowerCase().includes(this.search.toLowerCase()) ||
					note.body?.toLowerCase().includes(this.search.toLowerCase())
				);
			});
		} else {
			this.ngOnInit();
		}
	}
}
<main class="flex flex-col items-center justify-center gap-4">
	<h1>All Publications</h1>

	<div>
		<input
			type="text"
			placeholder="Search"
			class="input-group w-full rounded-md border-gray-300 border-2"
			[(ngModel)]="search"
			onChange="searchNotes()"
		/>
	</div>

	<ul class="flex flex-col items-center justify-center gap-4">
		<li *ngFor="let note of notes">
			<app-note
				[noteTitle]="note.title"
				[noteText]="note.body"
				[date]="note.updatedAt"
				[user]="note.User?.name"
			></app-note>
		</li>
	</ul>
</main>
