import { Component } from '@angular/core';

@Component({
  selector: 'app-welcome-image',
  templateUrl: './welcome-image.component.html',
  styleUrls: ['./welcome-image.component.scss']
})
export class WelcomeImageComponent {

}
<img
	src="../../assets/image_main.png"
	alt="main image"
/>

<div class="flex flex-col items-center justify-center">
	<p class="login1">Easy Media</p>
	<p class="login2 py-2">Now, share is easy</p>
	<p class="login3">Sign In</p>
</div>
