import { Component, ElementRef, ViewChild, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { FormGroup, FormControl, Validators } from '@angular/forms';


@Component({
	selector: 'app-home',
	// standalone: true,
	// imports: [CommonModule],
	templateUrl: './home.component.html',
	styleUrls: ['./home.component.scss'],
})
export class HomeComponent {
	title = 'world';

	isHighlighted = true;

	inputName = 'my input';
	linkStyle = 'underline';

	login() {
		alert('login');
	}
	isRed = false;
	change() {
		this.isRed = !this.isRed;
	}

	products = ['apple', 'orange', 'banana'];

	loggedIn = true;

	router = inject(Router);
	navigate() {
		this.router.navigateByUrl('/notes');
	}




	loginForm = new FormGroup({
		email: new FormControl('', Validators.required),
		password: new FormControl('', Validators.required),
	});

	email: string = '';

	password: string = '';

	input = 'password'

	login2() {
		alert(this.loginForm.value.email + ' | ' + this.loginForm.value.password);
	}

	showPassword = false;

	toggleShow() {
		this.showPassword = !this.showPassword;
		this.input = this.showPassword ? 'text' : 'password';
	}

}
<p>hello {{ title }}!</p>

<p [class.highlighted]="isHighlighted">This is some text</p>
<input
	type="text"
	[attr.aria-label]="inputName"
/>
<p [style.text-decoration]="linkStyle">Login</p>

<button (click)="login()">Click me</button>
<p [class.red]="isRed">some text for our page</p>
<button (click)="change()">Switch Background Color</button>

<div *ngFor="let item of products">{{ item }}</div>

<div *ngIf="loggedIn">Welcome!</div>

<button (click)="navigate()">Notes</button>

<h1 class="text-3xl font-bold underline">Hello world!</h1>



<main class="flex flex-col items-center justify-center">

    <img src="../../assets/image_main.png" alt="main image">

    <div class="flex flex-col items-center justify-center">
        <p class="login1">Easy Media</p>
        <p class="login2 paddin">Now, share is easy</p>
        <p class="login3">Sign In</p>
    </div>

    <form
        [formGroup]="loginForm"
        (ngSubmit)="login2()"
    >
        <div class="flex flex-col">
            <label for="email" class="label-form">Email</label>
            <input
                type="email"
                formControlName="email"
                placeholder="example@email.com"
                class="input-group"
            />
        </div>
        <div class="flex flex-col">
            <label for="password" class="label-form">Password</label>
            <div class="input-group">
                <input
                    type="{{input}}"
                    formControlName="password"
                    placeholder="······"
                    style="font-size: large;"
                    id="password"
                />
                <button
                    type="button"
                    class="btn btn-outline-secondary"
                    (click)="toggleShow()"
                    class="btn-show"
                >
                    <!-- <span class="eye"></span> -->
                </button>
            </div>
        </div>
        <div>
            <input
                type="submit"
                value="SIGN IN"
                [disabled]="!loginForm.valid"
                class="submit-login"
            />
        </div>
    </form>



    <span class="flex">
        <p class="signup-text">Don’t have an account?</p>
        <a class="signup-text2">Sign up here</a>
    </span>
</main>
