import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { FormGroup, FormControl, Validators } from '@angular/forms';


@Component({
	selector: 'app-home',
	// standalone: true,
	// imports: [CommonModule],
	templateUrl: './home.component.html',
	styleUrls: ['./home.component.scss'],
})
export class HomeComponent {
	title = 'world';

	isHighlighted = true;

	inputName = 'my input';
	linkStyle = 'underline';

	login() {
		alert('login');
	}
	isRed = false;
	change() {
		this.isRed = !this.isRed;
	}

	products = ['apple', 'orange', 'banana'];

	loggedIn = true;

	router = inject(Router);
	navigate() {
		this.router.navigateByUrl('/notes');
	}

	loginForm = new FormGroup({
		email: new FormControl('', Validators.required),
		password: new FormControl('', Validators.required),
	});

	login2() {
		alert(
			this.loginForm.value.email + ' | ' + this.loginForm.value.password
		);
	}
}
<p>hello {{ title }}!</p>

<p [class.highlighted]="isHighlighted">This is some text</p>
<input
	type="text"
	[attr.aria-label]="inputName"
/>
<p [style.text-decoration]="linkStyle">Login</p>

<button (click)="login()">Click me</button>
<p [class.red]="isRed">some text for our page</p>
<button (click)="change()">Switch Background Color</button>

<div *ngFor="let item of products">{{ item }}</div>

<div *ngIf="loggedIn">Welcome!</div>

<button (click)="navigate()">Notes</button>

<h1 class="text-3xl font-bold underline">Hello world!</h1>

<p class="text-3xl not-italic font-extrabold">Easy Media</p>
<p class="text-teal-400 text-sm not-italic font-normal">Now, share is easy</p>
<p class="text-xl not-italic font-normal">Sign In</p>

<form
	[formGroup]="loginForm"
	(ngSubmit)="login2()"
>
	<p>
		<input
			type="email"
			formControlName="email"
		/>
	</p>
	<p>
		<input
			type="password"
			formControlName="password"
		/>
	</p>
	<p>
		<input
			type="submit"
			value="SIGN IN"
			[disabled]="!loginForm.valid"
			class="w-56 h-8 flex-shrink-0 text-xl not-italic font-semibold"
		/>
	</p>
</form>

<span class="">
	<p class="text-gray-500 not-italic font-medium text-[10px]">Don’t have an account?</p>
	<a class="text-teal-400 not-italic font-medium text-[10px] underline">Sign up here</a>
</span>
