import { Component,Rout } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
router = inject(Router);
	navigate() {
		this.router.navigateByUrl('/sing-up');
	}
}
}
