import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

@Component({
	selector: 'app-home',
	// standalone: true,
	// imports: [CommonModule],
	templateUrl: './home.component.html',
	styleUrls: ['./home.component.scss'],
})
export class HomeComponent {
	title = 'world';

	isHighlighted = true;

	inputName = 'my input';
	linkStyle = 'underline';

	login() {
		alert('login');
	}
	isRed = false;
	change() {
		this.isRed = !this.isRed;
	}

	products = ['apple', 'orange', 'banana'];

	loggedIn = true;

	router = inject(Router);
	navigate() {
		this.router.navigateByUrl('/notes');
	}

	loginForm = new FormGroup({
		username: new FormControl('', Validators.required),
		password: new FormControl('', Validators.required),
	});

	login() {
		alert(
			this.loginForm.value.username + ' | ' + this.loginForm.value.password
		);
	}
}
<p>hello {{ title }}!</p>

<p [class.highlighted]="isHighlighted">This is some text</p>
<input
	type="text"
	[attr.aria-label]="inputName"
/>
<p [style.text-decoration]="linkStyle">Login</p>

<button (click)="login()">Click me</button>
<p [class.red]="isRed">some text for our page</p>
<button (click)="change()">Switch Background Color</button>

<div *ngFor="let item of products">{{ item }}</div>

<div *ngIf="loggedIn">Welcome!</div>

<button (click)="navigate()">Notes</button>

<h1 class="text-3xl font-bold underline">Hello world!</h1>

<div
	class="p-4 mb-4 text-sm text-blue-800 rounded-lg bg-blue-50 dark:bg-gray-800 dark:text-blue-400"
	role="alert"
>
	<span class="font-medium">Info alert!</span> Change a few things up and try
	submitting again.
</div>



<p class="text-3xl not-italic font-extrabold">Easy Media</p>
<p class="text-teal-400 text-sm not-italic font-normal">Now, share is easy</p>
<p class="text-xl not-italic font-normal">Sign In</p>

<form
	[formGroup]="loginForm"
	(ngSubmit)="login()"
>
	<p>
		<input
			type="text"
			formControlName="username"
		/>
	</p>
	<p>
		<input
			type="password"
			formControlName="password"
		/>
	</p>
	<p>
		<input
			type="submit"
			value="Submit"
			[disabled]="!loginForm.valid"
		/>
		<input
			type="reset"
			value="Reset"
			(click)="loginForm.reset()"
		/>
	</p>
</form>
