import { Component, EventEmitter, Output } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
	selector: 'app-sing-up-form',
	templateUrl: './sing-up-form.component.html',
	styleUrls: ['./sing-up-form.component.scss'],
})
export class SingUpFormComponent {
	loginForm = new FormGroup({
		email: new FormControl('', Validators.required),
		password: new FormControl('', Validators.required),
	});

	email: string = '';

	password: string = '';

	input = 'password';

	login2() {
		alert(this.loginForm.value.email + ' | ' + this.loginForm.value.password);
	}

	showPassword = false;

	toggleShow() {
		this.showPassword = !this.showPassword;
		this.input = this.showPassword ? 'text' : 'password';
	}

	@Output() emailSubmitted = new EventEmitter();
	@Output() passwordSubmitted = new EventEmitter();

	onSubmit() {
		if (this.loginForm.valid) {
			const email = this.loginForm.value.email;
			this.emailSubmitted.emit(email);
		}
		if (this.loginForm.valid) {
			const password = this.loginForm.value.password;
			this.passwordSubmitted.emit(password);
		}
		console.log(this.loginForm.value);
	}
}
<form
	[formGroup]="loginForm"
	(ngSubmit)="onSubmit()"
	class="flex flex-col items-center justify-center gap-6"
>
	<div class="flex flex-col">

		<label
			for="email"
			class="label-form"
			>Email</label
		>
		<input
			type="email"
			formControlName="email"
			placeholder="example@email.com"
			class="input-group"
			required
		/>

		<label
			for="email"
			class="label-form"
			>Email</label
		>
		<input
			type="email"
			formControlName="email"
			placeholder="example@email.com"
			class="input-group"
			required
		/>
	</div>
	<div class="flex flex-col">
		<label
			for="password"
			class="label-form"
			>Password</label
		>
		<div class="input-group w-full">
			<input
				type="{{ input }}"
				formControlName="password"
				placeholder="······"
				style="font-size: large"
				id="password"
				required
				class="w-4/5"
			/>
			<button
				type="button"
				class="btn btn-outline-secondary"
				(click)="toggleShow()"
				class="btn-show ml-6"
			>
				<span class="eye"></span>
			</button>
		</div>
	</div>
	<div>
		<input
			type="submit"
			value="SIGN IN"
			[disabled]="!loginForm.valid"
			class="submit-login"
		/>
	</div>
</form>
