import { Component, inject } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { NOTES } from '../../notes';
import { Router } from '@angular/router';

@Component({
	selector: 'app-add-note',
	templateUrl: './add-note.component.html',
	styleUrls: ['./add-note.component.scss'],
})
export class AddNoteComponent {
	noteTitle = 'Your post title';
	noteText = 'Create message for share with your friends.';
  // time and short date
  date = new Date();
  formattedDate = this.date.toLocaleTimeString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true }) + ' ' + date.toLocaleDateString('en-US', { year: '2-digit', month: '2-digit', day: '2-digit' }).replace(/\//g, '-');
  console.log(formattedDate);

  user = 'User';

	addNoteForm = new FormGroup({
		title: new FormControl('', Validators.required),
		text: new FormControl(''),
	});

	onSubmit() {
		alert(this.addNoteForm.value.title + ' | ' + this.addNoteForm.value.text);
	}

	router = inject(Router);
	addNote() {
		let title = this.addNoteForm.value.title ?? '';
		let text = this.addNoteForm.value.text ?? '';

		if (this.addNoteForm.valid) {
			let ids = NOTES.map((a) => a.id);
			let maxId = 0;
			if (ids.length > 0) {
				maxId = Math.max(...ids);
			}
			let newNote = {
				id: maxId + 1,
				title: title,
				text: text,
			};
			NOTES.unshift(newNote);
			this.addNoteForm.reset();
			this.router.navigateByUrl('/notes-list');
		}
	}

}
<main class="flex flex-col items-center">
	<h1>Create message</h1>
	<form
		[formGroup]="addNoteForm"
		(ngSubmit)="addNote()"
	>
		<div class="form-group flex flex-col">
			<label for="title">Title messages</label>
			<input
				type="text"
				class="form-control"
				id="title"
				formControlName="title"
        placeholder="Your post title"
        maxlength="30"
			/>
      <p class="text-create-note">{{ addNoteForm.value.title?.length }}/30</p>
		</div>
		<div class="form-group flex flex-col">
			<label for="text">Content</label>
			<input
				type="text"
				class="form-control"
				id="text"
				formControlName="text"
        placeholder="Create message for share with your friends."
        maxlength="300"
			/>
      	<p class="text-create-note">{{ addNoteForm.value.text?.length }}/300</p>
		</div>
		<button
			type="submit"
			class="btn btn-primary"
			[disabled]="!addNoteForm.valid"
		>
			SHARE
		</button>
	</form>

  <div class="note">
    <div class="note-title">
      <h2 aria-placeholder="asdsf">
        {{ noteTitle }}
      </h2>
    </div>
    <div class="note-text">
      <p>
        {{ noteText}}
      </p>
      <div class="flex">
        <p>{{date}}</p>
        <p>{{user}}</p>
      </div>
    </div>
  </div>
</main>
